import React, { useState } from 'react';
import { Form, Button, Card, Alert, Spinner } from 'react-bootstrap';
import axios from 'axios';

const UrlInput = ({ 
  setUrls, 
  setContent, 
  setLoading, 
  setError, 
  setContentLoaded,
  setProcessedUrls
}) => {
  const [urlInput, setUrlInput] = useState('');
  const [urlList, setUrlList] = useState([]);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [localError, setLocalError] = useState('');

  const handleUrlChange = (e) => {
    setUrlInput(e.target.value);
  };

  const addUrl = () => {
    if (urlInput.trim() === '') return;
    
    // Basic URL validation
    try {
      new URL(urlInput);
      
      if (!urlList.includes(urlInput)) {
        setUrlList([...urlList, urlInput]);
      } else {
        setLocalError('URL already added');
        setTimeout(() => setLocalError(''), 3000);
      }
      setUrlInput('');
    } catch (error) {
      setLocalError('Please enter a valid URL');
      setTimeout(() => setLocalError(''), 3000);
    }
  };

  const removeUrl = (index) => {
    const newUrlList = [...urlList];
    newUrlList.splice(index, 1);
    setUrlList(newUrlList);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (urlList.length === 0) {
      setLocalError('Please add at least one URL');
      setTimeout(() => setLocalError(''), 3000);
      return;
    }

    setLoading(true);
    setIsSubmitting(true);
    setError('');
    setContentLoaded(false);

    try {
      const response = await axios.post('http://localhost:5000/api/extract', { urls: urlList });
      setUrls(urlList);
      setContent(response.data.content);
      setContentLoaded(true);
      setProcessedUrls(urlList); // Store processed URLs
    } catch (error) {
      console.error('Error extracting content:', error);
      setError(error.response?.data?.error || 'Failed to extract content from URLs');
    } finally {
      setLoading(false);
      setIsSubmitting(false);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      e.preventDefault();
      addUrl();
    }
  };

  const clearAllUrls = () => {
    setUrlList([]);
  };

  return (
    <Card className="shadow-sm">
      <Card.Body>
        <Card.Title>Enter URLs to Extract Content</Card.Title>
        <Form onSubmit={handleSubmit}>
          <Form.Group className="mb-3">
            <Form.Label>Website URLs</Form.Label>
            <div className="d-flex">
              <Form.Control
                type="text"
                placeholder="Enter URL (e.g., https://example.com)"
                value={urlInput}
                onChange={handleUrlChange}
                onKeyDown={handleKeyPress}
              />
              <Button variant="secondary" className="ms-2" onClick={addUrl}>
                Add
              </Button>
            </div>
            {localError && (
              <Alert variant="danger" className="mt-2 p-2" style={{ fontSize: '0.9rem' }}>
                {localError}
              </Alert>
            )}
          </Form.Group>

          {urlList.length > 0 && (
            <div className="mb-3">
              <div className="d-flex justify-content-between align-items-center mb-2">
                <span>URLs to extract content from:</span>
                <Button variant="outline-danger" size="sm" onClick={clearAllUrls}>
                  Clear All
                </Button>
              </div>
              <ul className="list-group">
                {urlList.map((url, index) => (
                  <li key={index} className="list-group-item d-flex justify-content-between align-items-center">
                    <span className="text-truncate" style={{ maxWidth: '80%' }}>{url}</span>
                    <Button variant="outline-danger" size="sm" onClick={() => removeUrl(index)}>
                      Remove
                    </Button>
                  </li>
                ))}
              </ul>
            </div>
          )}

          <Button 
            variant="primary" 
            type="submit" 
            disabled={urlList.length === 0 || isSubmitting}
            className="d-flex align-items-center"
          >
            {isSubmitting ? (
              <>
                <Spinner
                  as="span"
                  animation="border"
                  size="sm"
                  role="status"
                  aria-hidden="true"
                  className="me-2"
                />
                Extracting...
              </>
            ) : (
              'Extract Content'
            )}
          </Button>
        </Form>
      </Card.Body>
    </Card>
  );
};

export default UrlInput;
